const list_of_questions = $json.output;
const relevant_context = $('Set Relevant Context').first().json.relevant_context;
var list_of_full_message = [];
for (const question of list_of_questions) {
  list_of_full_message.push(
    {
      'question': question.question,
      'reason': question.reason,
      'proposal_law': $('User Query').first().json.chatInput,
      'relevant_context': relevant_context
    }
  );
}

return list_of_full_message;

const list_of_improved_questions = $input.all().map(item => item.json.output);
const relevant_context = $('Set Relevant Context').first().json.relevant_context;
const proposal_law = $('User Query').first().json.chatInput;
var list_of_enhanced_message = [];
for (const question of list_of_improved_questions) {
  list_of_enhanced_message.push(
    {
      'question': question.rewritten_question,
      'reason': question.rewritten_reason,
      'proposal_law': proposal_law,
      'relevant_context': relevant_context
    }
  );
}

return list_of_enhanced_message;

// 1st refined questions
const first_refined_questions = $('Refine 1').all().map(item=>item.json.output);
// 2nd refined questions
const second_refined_questions = $('Refine 2').all().map(item=>item.json.output);

const list_of_questions = [...first_refined_questions, ...second_refined_questions];
const proposal_law = $('User Query').first().json.chatInput;
const relevant_context = $('Set Relevant Context').first().json.relevant_context;
var list_of_full_questions = [];
for (const question of list_of_questions) {
  list_of_full_questions.push(
    {
      'proposal_law': proposal_law,
      'question': question.rewritten_question,
      'reason': question.rewritten_reason,
      'relevant_context': relevant_context
    }
  );
}

return list_of_full_questions;

var list_of_questions_with_new_context = [];
for (let i=0; i<$input.all().length; i++) {
  const question = $('Prepare Information for New Retrieval').all()[i].json;
  question.relevant_context = $input.all()[i].json.data.chunks.map(item=>item.content.trim()).join("\n\n=====================\n\n");
  list_of_questions_with_new_context.push(question);
}

return list_of_questions_with_new_context;
