var related_chunks = [];
for (let i=0; i<$('Retrieve From RAGFlow').first().json.data.chunks.length; i++) {
  let chunk =$('Retrieve From RAGFlow').first().json.data.chunks[i];
  related_chunks.push(
    {
      'dataset_id': chunk.dataset_id,
      'document_id': chunk.document_id,
      'chunk_id': chunk.id,
      'keywords': chunk.important_keywords,
      'content': chunk.content
    }
  );
}

return {related_chunks:related_chunks};

let chunks=$input.first().json.related_chunks;
let result="";
if (chunks){
  result=chunks.map(item=>item.content).join("\n=================\n")
}
return {'relevant_context': result};

